    private void exportButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_exportButtonActionPerformed
        // TODO add your handling code here:
        JFileChooser fc = new JFileChooser();
        fc.setCurrentDirectory(new File("./data/"));
        fc.setSelectedFile(new File(""));
        fc.setFileFilter(new FileNameExtensionFilter("data files only", "data"));
        int ret = fc.showSaveDialog(null);
        if (ret == JFileChooser.APPROVE_OPTION) {
            File file = fc.getSelectedFile();
            String fname = file.getAbsolutePath();
            System.out.println(fname);
            if (!fname.endsWith(".data")) {
                file = new File(fname + ".data");
            }
                try {
                    IO io = new IO();
                    io.write(file.getAbsolutePath(), b);
                } catch (Exception e) {
                    System.out.println("Error");
                }
            // }
        }


    }//GEN-LAST:event_exportButtonActionPerformed


    private void inportButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_inportButtonActionPerformed
        // TODO add your handling code here:
        JFileChooser fc = new JFileChooser();
        fc.setCurrentDirectory(new File("./data/"));
        fc.setSelectedFile(new File(""));
        fc.setFileFilter(new FileNameExtensionFilter("data files only", "data"));
        int ret = fc.showOpenDialog(null);
        if (ret == JFileChooser.APPROVE_OPTION) {
            File file = fc.getSelectedFile();
            String fname = file.getAbsolutePath();
            System.out.println(fname);
            try {
                IO io = new IO();
                b = io.read(file.getAbsolutePath());
                this.theBoard.update(b.getBoard(), this.theBoard.getPencilMarkings());
                this.repaint();
            } catch (Exception e) {
                System.out.println("Error");
            }
        }
    }//GEN-LAST:event_inportButtonActionPerformed

