    private void inputHanddler(int z) {
        if (reg == 0) {
            return;
        }
        if (num == 0) {
            return;
        }
        int x = 0;
        int y = 0;

        y += (this.reg - 1) / 3 * 3;
        x += (reg - 1) % 3 * 3;

        y += (num - 1) / 3;
        x += (num - 1) % 3;

        if (!b.getBoard()[x][y].getGiven()) {
            b.getBoard()[x][y].setValue(z);
        }

        this.repaint();
    }

